import React, { useState } from "react";

const BasicForm = (props) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState(true);

  const firstNameHandler = (event) => {
    setFirstName(event.target.value);
  };

  const lastNameHandler = (event) => {
    setLastName(event.target.value);
  };

  const emailHandler = (event) => {
    setEmail(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setError(false)
    if (firstName.trim() === ''){
      setError(true)
    }
    if (lastName.trim() === ''){
      setError(true)
    }
    if (!email.includes('@')){
      setError(true)
    }

    setFirstName('')
    setLastName('')
    setEmail('')
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameHandler}
            value={firstName}
          />
        </div>
        {!error && <p style={{color:'red'}}>First Name should not be empty</p>}
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            onChange={lastNameHandler}
            value={lastName}
          />
        </div>
        {!error && <p style={{color:'red'}}>Last Name should not be empty</p>}
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" onChange={emailHandler} value={email} />
      </div>
      {!error && <p style={{color:'red'}}>Email should be valid</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
