import React, { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(true);

  const nameInputTextHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const submitTextHandler = (event) => {
    event.preventDefault();
    if(enteredName.trim() === ''){
      setEnteredNameIsValid(false)
      return;
    }
    setEnteredNameIsValid(true);
    console.log(enteredName, "form");
  };

  const nameInputClasses = enteredNameIsValid ? 'form-control' : 'form-control invalid'

  return (
    <form onSubmit={submitTextHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputTextHandler}
          value={enteredName}
        />
        {!enteredNameIsValid && <p style={{color:'red'}}>Name should not be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
