import React, { useState, useEffect } from "react";

const BasicForm = (props) => {
  const [firstName, setFirstName] = useState("");
  const [firstNameTocuhed, setFirstNameTouched] = useState(false);
  const [formIsValid, setFormIsValid] = useState(false);

  const firstNameIsValid = firstName.trim() !== '';
  const nameInputIsInvalid = !firstNameIsValid && firstNameTocuhed;

  useEffect(()=>{
    if(firstNameIsValid){
      setFormIsValid(true)
    } else {
      setFormIsValid(false)
    }
  }, [firstNameIsValid])

  const nameInputChangeHandler = (event) => {
    setFirstName(event.target.value);
  };

  const nameInputBlurHandler = (event) => {
    setFirstNameTouched(true);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setFirstNameTouched(true)

    if (!firstNameIsValid) {
      return;
    }

    setFirstName("");
    setFirstNameTouched(false);
  };


  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={nameInputClasses}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={nameInputChangeHandler}
            onBlur={nameInputBlurHandler}
            value={firstName}
          />
          {nameInputIsInvalid && <p style={{color:'red'}}>Name should not be empty</p>}
        </div>
        {/* <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div> */}
      </div>
      {/* <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div> */}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
